using Application.Interfaces;
using FileSignatures;
using Helpers.Enums;
using Shouldly;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Reflection;
using System.Text;
using System.Threading.Tasks;
using Utilities.Extenstions;
using Utilities.Services;
using Xunit;

namespace BoilerPlate.Testing.Utilities
{
    public class FileValidatorTests
    {
        private readonly IFileValidator _fileValidator;
        private readonly string _fileDirectory;
        public FileValidatorTests()
        {
            var assembly = typeof(VideoFormatFile).GetTypeInfo().Assembly;
            var allFormats = FileFormatLocator.GetFormats(assembly, true);
            var inspector = new FileFormatInspector(allFormats);

            _fileValidator = new FileValidator(inspector);
            _fileDirectory = Directory.GetParent(Environment.CurrentDirectory).Parent.Parent.FullName + "/_shared/files";
        }

        [Fact]
        public void Is_Valid_Png_LessThanOrEqual_1_Mega()
        {
            var base64Image = "";

            byte[] file = Convert.FromBase64String(base64Image);

            var result = _fileValidator.IsValidFile(file, 1, new[] { FileExtensions.png });

            result.IsSuccess.ShouldBe(true);
        }

        [Fact]
        public void Not_Valid_Image_GreaterThan_1_Mega()
        {
            var path = Path.Combine(_fileDirectory, "large_image.jpg");

            byte[] file = File.ReadAllBytes(path);

            var result = _fileValidator.IsValidFile(file, 1, new[] { FileExtensions.png, FileExtensions.jpg });

            result.IsSuccess.ShouldBe(false);
        }

        [Fact]
        public void Valid_Video_LessThanOrEqual_6_Mega()
        {
            var path = Path.Combine(_fileDirectory, "video_file.mp4");

            byte[] file = File.ReadAllBytes(path);

            var result = _fileValidator.IsValidFile(file, 6, new[] { FileExtensions.mp4 });

            result.IsSuccess.ShouldBe(true);
        }

        [Fact]
        public void Valid_PDF_LessThanOrEqual_1_Mega()
        {
            var path = Path.Combine(_fileDirectory, "pdf_file.pdf");

            byte[] file = File.ReadAllBytes(path);

            var result = _fileValidator.IsValidFile(file, 1, new[] { FileExtensions.pdf });

            result.IsSuccess.ShouldBe(true);
        }

        [Fact]
        public void Valid_Word_Files_Size_LessThanOrEqual_1_Mega()
        {
            var path_doc = Path.Combine(_fileDirectory, "word_file.doc");
            var path_docx = Path.Combine(_fileDirectory, "word_file.docx");

            byte[] file_doc = File.ReadAllBytes(path_doc);
            byte[] file_docx = File.ReadAllBytes(path_docx);

            var result_doc = _fileValidator.IsValidFile(file_doc, 1, new[] { FileExtensions.doc, FileExtensions.docx });
            var result_docx = _fileValidator.IsValidFile(file_docx, 1, new[] { FileExtensions.doc, FileExtensions.docx });

            result_doc.IsSuccess.ShouldBe(true);
            result_docx.IsSuccess.ShouldBe(true);
        }

        [Fact]
        public void Valid_Excel_Files_Size_LessThanOrEqual_1_Mega()
        {
            var path_xls = Path.Combine(_fileDirectory, "excel_file.xls");
            var path_xlsx = Path.Combine(_fileDirectory, "excel_file.xlsx");

            byte[] file_xls = File.ReadAllBytes(path_xls);
            byte[] file_xlsx = File.ReadAllBytes(path_xlsx);

            var result_xls = _fileValidator.IsValidFile(file_xls, 1, new[] { FileExtensions.xls, FileExtensions.xlsx });
            var result_xlsx = _fileValidator.IsValidFile(file_xlsx, 1, new[] { FileExtensions.xls, FileExtensions.xlsx });

            result_xls.IsSuccess.ShouldBe(true);
            result_xlsx.IsSuccess.ShouldBe(true);
        }

        [Fact]
        public void Not_Valid_PDF()
        {
            var path = Path.Combine(_fileDirectory, "invalid_file.pdf");

            byte[] file = File.ReadAllBytes(path);

            var result = _fileValidator.IsValidFile(file, 1, new[] { FileExtensions.pdf });

            result.IsSuccess.ShouldBe(false);
        }
    }
}
